import requests

def get_age(name):
    url = f'https://api.agify.io?name={name}'
    response = requests.get(url)
    if response.status_code == 200:
        data = response.json()
        return data['age']
    else:
        return None

name = 'Junko'
print(f'The age of {name} is {get_age(name)}')